import {createSelector} from "reselect";
import {getField} from "../../utils/reducer/reducer.utils";

const selectCategoriesReducer = (state) => state.categories;

export const selectCategories = createSelector([selectCategoriesReducer], (categoriesSlice) => getField("categories", categoriesSlice))

const createCategoriesMap = (categories) => categories.reduce((acc, category) => {
  const {title, items} = category;
  acc[title.toLowerCase()] = items;
  return acc;
}, {});

export const selectCategoriesMap = createSelector([selectCategories], createCategoriesMap)
