import {createContext, useEffect, useReducer, useState} from "react";

import {createAction} from "../utils/reducer/reducer.utils";

const addCartItem = (cartItems, productToAdd) => {
  const existingCartItem = cartItems.find(
    (cartItem) => cartItem.id === productToAdd.id
  );
  if (existingCartItem) {
    return cartItems.map((cartItem) =>
      cartItem.id === productToAdd.id
        ? {
            ...cartItem,
            quantity: cartItem.quantity + 1,
          }
        : cartItem
    );
  }

  return [...cartItems, { ...productToAdd, quantity: 1 }];
};

const removeCartItem = (cartItems, cartItemToRemove) => {
  return cartItems.reduce(
    (totalCartItems, cartItem) =>
      cartItem.id === cartItemToRemove.id
        ? cartItem.quantity === 1
          ? totalCartItems
          : totalCartItems.concat({
              ...cartItem,
              quantity: cartItem.quantity - 1,
            })
        : totalCartItems.concat(cartItem),
    []
  );
};

const clearCartItem = (cartItems, cartItemToClear) =>
  cartItems.filter((cartItem) => cartItem.id !== cartItemToClear.id);


export const CartContext = createContext({
  isCartOpen: false,
  cartTotal: 0,
  cartItems: [],
  setIsCartOpen: () => {},
  addItemToCart: () => {},
  removeItemFromCart: () => {},
  clearItemFromCart: () => {},
});

const CART_ACTIONS = {
  SET_CART_ITEMS: "SET_CART_ITEMS",
  SET_IS_CART_OPEN: "SET_IS_CART_OPEN"
}

const INITIAL_STATE = {
  isCartOpen: false,
  cartTotal: 0,
  cartItems: [],
  cartCount: 0
}

const init = ({isCartOpen, cartTotal, cartItems, cartCount}) => ({
  isCartOpen,
  cartTotal,
  cartItems,
  cartCount
})


const cartReducer = (state, action) => {
  const {type, payload} = action;
  switch (type) {
    case CART_ACTIONS.SET_CART_ITEMS:
      return {...state, ...payload}
    case CART_ACTIONS.SET_IS_CART_OPEN:
      return {...state, isCartOpen: payload}
    default:
      throw new Error(`Unhandled type ${type} in cartReducer`)
  }
}



export const CartProvider = ({ children }) => {
  const [{cartItems, cartCount, cartTotal, isCartOpen}, dispatch] = useReducer(cartReducer, INITIAL_STATE, init)

  const updateCartItemsReducer = (newCartItems) => {
    const newCartCount = ((cartItems) =>
        cartItems.reduce((acc, next) => acc + next.quantity, 0))(newCartItems);

    const newCartTotal = ((cartItems) =>
        cartItems.reduce(
            (total, cartItem) => total + cartItem.quantity * cartItem.price,
            0
        ))(newCartItems);
    dispatch({type: CART_ACTIONS.SET_CART_ITEMS, payload: {cartItems: newCartItems, cartTotal: newCartTotal, cartCount: newCartCount}})
  }

  const addItemToCart = (productToAdd) => {
    const newCartItems = addCartItem(cartItems, productToAdd);
    updateCartItemsReducer(newCartItems)
  };

  const removeItemFromCart = (cartItemToRemove) => {
    const newCartItems = removeCartItem(cartItems, cartItemToRemove);
    updateCartItemsReducer(newCartItems)
  };

  const clearItemFromCart = (cartItemToClear) => {
    const newCartItems = clearCartItem(cartItems, cartItemToClear);
    updateCartItemsReducer(newCartItems)
  };

  const setIsCartOpen = (bool ) => dispatch({type: CART_ACTIONS.SET_IS_CART_OPEN, payload: bool})

  const value = {
    isCartOpen,
    setIsCartOpen,
    addItemToCart,
    removeItemFromCart,
    clearItemFromCart,
    cartItems,
    cartCount,
    cartTotal,
  };

  return <CartContext.Provider value={value}>{children}</CartContext.Provider>;
};
