import {createContext, useEffect, useReducer, useState} from "react";

const addCartItem = (cartItems, productToAdd) => {
  const existingCartItem = cartItems.find(
    (cartItem) => cartItem.id === productToAdd.id
  );
  if (existingCartItem) {
    return cartItems.map((cartItem) =>
      cartItem.id === productToAdd.id
        ? {
            ...cartItem,
            quantity: cartItem.quantity + 1,
          }
        : cartItem
    );
  }

  return [...cartItems, { ...productToAdd, quantity: 1 }];
};

const removeCartItem = (cartItems, cartItemToRemove) => {
  return cartItems.reduce(
    (totalCartItems, cartItem) =>
      cartItem.id === cartItemToRemove.id
        ? cartItem.quantity === 1
          ? totalCartItems
          : totalCartItems.concat({
              ...cartItem,
              quantity: cartItem.quantity - 1,
            })
        : totalCartItems.concat(cartItem),
    []
  );
};

const clearCartItem = (cartItems, cartItemToClear) =>
  cartItems.filter((cartItem) => cartItem.id !== cartItemToClear.id);


export const CartContext = createContext({
  isCartOpen: false,
  cartTotal: 0,
  cartItems: [],
  setIsCartOpen: () => {},
  addItemToCart: () => {},
  removeItemFromCart: () => {},
  clearItemFromCart: () => {},
});

const CART_ACTIONS = {
  ADD_ITEMS_TO_CART: "ADD_ITEMS_TO_CART",
  REMOVE_ITEMS_FROM_CART: "REMOVE_ITEMS_FROM_CART",
  CLEAR_ITEMS_FROM_CART: "CLEAR_ITEMS_FROM_CART",
  SET_CART_IS_OPEN: "SET_CART_ITEM",
  SET_CART_COUNT: "SET_CART_COUNT"
}

const init = ({isCartOpen, cartTotal, cartItems, cartCount}) => ({
  isCartOpen,
  cartTotal,
  cartItems,
  cartCount
})

const cartReducer = (state, action) => {
  const { type, payload } = action;

  switch (type) {
    case CART_ACTIONS.ADD_ITEMS_TO_CART:
      return {...state, cartItems: addCartItem(state.cartItems, payload)}
    case CART_ACTIONS.CLEAR_ITEMS_FROM_CART:
      return {...state, cartItems: clearCartItem(state.cartItems, payload)}
    case CART_ACTIONS.REMOVE_ITEMS_FROM_CART:
      return {...state, cartItems: removeCartItem(state.cartItems, payload)}
    case CART_ACTIONS.SET_CART_IS_OPEN:
      return {...state, isCartOpen: payload }
    case CART_ACTIONS.SET_CART_COUNT:
      return {...state, cartCount: payload }
    default:
      throw new Error(`Unhandled type ${type} in cartReducer`)
  }
}

export const CartProvider = ({ children }) => {
  // const [isCartOpen, setIsCartOpen] = useState(false);
  // const [cartItems, setCartItems] = useState([]);
  // const [cartCount, setCartCount] = useState(0);
  // const [cartTotal, setCartTotal] = useState(0);

  const [{isCartOpen, cartItems, cartCount, cartTotal}, dispatch] = useReducer(cartReducer, {isCartOpen: false, cartTotal: 0, cartItems: [], cartCount: 0}, init);

  const setCartTotal = newCartCount => dispatch({type: CART_ACTIONS.SET_CART_COUNT, payload: newCartCount})

  const setIsCartOpen = isCartOpen => dispatch({type: CART_ACTIONS.SET_CART_IS_OPEN, payload: isCartOpen})

  const addCartItem = productToAdd => dispatch({type: CART_ACTIONS.ADD_ITEMS_TO_CART, payload: productToAdd})


  useEffect(() => {
    const newCartCount = (cartItems) =>
      cartItems.reduce((acc, next) => acc + next.quantity, 0);
    setCartCount(newCartCount(cartItems));
  }, [cartItems]);

  useEffect(() => {
    const newCartTotal = (cartItems) =>
      cartItems.reduce(
        (total, cartItem) => total + cartItem.quantity * cartItem.price,
        0
      );
    setCartTotal(newCartTotal(cartItems));
  }, [cartItems]);

  const addItemToCart = (productToAdd) =>
    addCartItem(productToAdd);

  const removeItemFromCart = (cartItemToRemove) => {
    setCartItems(removeCartItem(cartItems, cartItemToRemove));
  };

  const clearItemFromCart = (cartItemToClear) => {
    setCartItems(clearCartItem(cartItems, cartItemToClear));
  };

  const value = {
    isCartOpen,
    setIsCartOpen,
    addItemToCart,
    removeItemFromCart,
    clearItemFromCart,
    cartItems,
    cartCount,
    cartTotal,
  };

  return <CartContext.Provider value={value}>{children}</CartContext.Provider>;
};
