import {createSelector} from "reselect";

const selctCategoriesReducer =  (state) => state.categories;

export const selectCategoriesMap = ({categories}) => categories.categories.reduce((acc, category) => {
  const {title, items} = category;
  acc[title.toLowerCase()] = items;
  return acc;
}, {});
