import {useDispatch, useSelector} from "react-redux";
import {addItemToCart} from "../../store/cart/cart.action";
import {selectCartItems} from "../../store/cart/cart.selector";

import {Footer, Image, Name, Price, ProductCardContainer} from "./product-card.styles";

import {Button, BUTTON_TYPES} from "../button/button.component";

export const ProductCard = ({product}) => {
  const cartItems = useSelector(selectCartItems)
  const dispatch = useDispatch();

  const {name, price, imageUrl} = product;

  const addProductToCart = (product) => dispatch(addItemToCart(cartItems, product));

  return (
    <ProductCardContainer>
      <Image src={imageUrl} alt={`${name}`}/>
      <Footer>
        <Name>{name}</Name>
        <Price>{price}</Price>
      </Footer>
      <Button buttonType={BUTTON_TYPES.inverted} onClick={() => addProductToCart(product)}>
        Add to cart
      </Button>
    </ProductCardContainer>
  );
};
