import { useContext } from "react";
import {ProductCardContainer, Image, Price, Name, Footer} from "./product-card.styles";

import { CartContext } from "../../contexts/cart.context";
import { Button, BUTTON_TYPES } from "../button/button.component";

export const ProductCard = ({ product }) => {
  const { name, price, imageUrl } = product;
  const { addItemToCart } = useContext(CartContext);

  const addProductToCart = (product) => addItemToCart(product);

  return (
    <ProductCardContainer>
      <Image src={imageUrl} alt={`${name}`} />
      <Footer>
        <Name>{name}</Name>
        <Price>{price}</Price>
      </Footer>
      <Button buttonType={BUTTON_TYPES.inverted} onClick={() => addProductToCart(product)}>
        Add to cart
      </Button>
    </ProductCardContainer>
  );
};
