import "./form-input.styles";

export const FormInput = ({label, ...otherProps}) => {

  return (
    <div className="group">
      <input className="form-input" {...otherProps} />
      { label &&
        <label className={`${otherProps.value.length ? "shrink" : ""} form-input-label`} htmlFor="confirmPassword">{label}</label>
      }
    </div>
  );

}
