import {createSelector} from "reselect";
import {getField} from "../../utils/reducer/reducer.utils";


export const getCartCount = (cartItems) => cartItems.reduce((acc, next) => acc + next.quantity, 0);
export const getCartTotal = (cartItems) => cartItems.reduce((total, cartItem) => total + cartItem.quantity * cartItem.price, 0);

export const selectCartSlice = ({cart}) => cart;

export const selectCartItems = createSelector([selectCartSlice], (cartSlice) =>
    getField("cartItems", cartSlice)
)

export const selectCartIsOpen = createSelector([selectCartSlice], (cartSlice) =>
    getField("isCartOpen", cartSlice)
)

export const selectCartCount = createSelector([selectCartItems], getCartCount)

export const selectCartTotal = createSelector([selectCartItems], getCartTotal)
